import React, {useEffect, useState} from 'react';
import axios from 'axios';
import swal from 'sweetalert2';
import withReactContent from 'sweetalert2-react-content';
import { show_alerta } from '../functions';

const ShowResult = () =>{

	const url = 'http://localhost/api-products-main/';
	const [results, setResultsAll] = useState([]);
	const [id, setId] = useState('');
	const [title, setTitle] = useState('');
	const [name, setName] = useState('');
	const [description, setDescription] = useState('');
	const [price, setPrice] = useState('');
	//const [testid, setTestId] = useState('');
	/*const [producto, setTestName] = useState('');
	const [description, setDescripcion] = useState('');
	const [precio, setPrecio] = useState('');*/
	/*const [partnumber, setPartNumber] = useState('');
	const [serialno, setSerialNo] = useState('');
	const [datetime, setDatetime] = useState('');
	const [duration, setDuration] = useState('');
	const [defaults, setDefaults] = useState('');
	const [resultsc, setResultsc] = useState('');*/
	const [operation, setOperation] = useState(1);

	useEffect( ()=>{
		getResults();
	}, []);

	const getResults = async () => {
		const respuesta = await axios.get(url);
		setResultsAll(respuesta.data);
	}
	
	const openModal = (op, id, name, description, price)=>{
		setId('');
		setName('');
		setDescription('');
		setPrice('');
		setOperation(op);
		if(op ===1){
			setTitle('Registrar Producto');
		}else if(op===2){
			setTitle('Editar Producto');
			setId(id);
			setName(name);
			setDescription(description);
			setPrice(price);
		}
		window.setTimeout(function(){
			document.getElementById('nombre').focus();
		},500);
	}
	
	const validar = () => {
		var parametros;
		var metodo;
		if(name.trim() === ''){
			show_alerta('Escribe el nombre del producto', 'warning');
		}else if(description.trim() === ''){
			show_alerta('Escribe una descripcion del producto', 'warning');
		}else if(price.trim() === ''){
			show_alerta('Escribe el precio del producto', 'warning');
		}else{
			if(operation === 1 ){
				parametros={name:name.trim(), description:description.trim(), price:price.trim() }
				metodo = 'POST';
			}else{				
				parametros={id:id, name:name.trim(), description:description.trim(), price:price.trim() }
				metodo = 'PUT';
			}
			enviarSolicitud(metodo, parametros);
		}
	}
	
	const enviarSolicitud = async(metodo, parametros) => {
		await axios( {method:metodo, url:url, data:parametros} ).then(function(respuestaApi){
			var tipo = respuestaApi.data[0];
			var msj = respuestaApi.data[1];
			show_alerta(msj, tipo);
			if(tipo === 'success'){
				document.getElementById('close').click();
				getResults();
			}
			
		})
		.catch(function(error){
			show_alerta('Erorr en la solicitud', 'error');
			console.log('error');
		});
	}
	
	const deleteProduct = (id, name)=>{
		const MySwal = withReactContent(swal);
		MySwal.fire({
			title:"¿Seguro de eliminar el item '+ name +' ?",
			icon:"question",  text:"No podra revertir",
			showCancelButton:true, confirmButtonText:"Sí, eliminar", cancelButtonText:"Cancelar"
		}).then((respuestaApi) =>{
			if(respuestaApi.isConfirmed){
				setId(id);
				enviarSolicitud('DELETE', {id:id});
			}else{
				show_alerta('El producto no fue eliminado', 'info');
			}
		}); 
	}

	return (
		<div className='App'>
			<div className='container-fluid'>
				<div className='row mt-3'>
					<div className='col-md-4 offset-4'>
						<div className='d-grid mx-auto'>
							<button onClick={()=>openModal(1)} className='btn btn-dark' data-bs-toggle='modal' data-bs-target='#modalResults'>
								<i className='fa-solid fa-circle-plus'></i>Añadir
							</button>
						</div>
					</div>
				</div>
				
				<div className='row mt-3'>
					<div className='col-md-4 offset-4'>
						<div className='table-responsive'>
							<table className='table table-bordered'>
								<thead>
									<tr><td>#</td><td>nombre</td><td>descripcion</td><td>precio</td><td>Accion</td></tr>
								</thead>
								<tbody className='table-group-divider'>
									{ results.map( (result, i )=>(
										<tr key={result.id}>
											<td>{(i+1)}</td>
											<td>{result.name}</td>
											<td>{result.description}</td>
											<td>$ {new Intl.NumberFormat('usd-eeuu').format( result.price )}</td>
											<td>
												<button onClick={() => openModal(2, result.id, result.name, result.description, result.price) } className='btn btn-warning' data-bs-toggle='modal' data-bs-target='#modalResults'>
													<i className='fa-solid fa-edit'></i> 
												</button>&nbsp;
												<button onClick={()=> deleteProduct(result.id)} className='btn btn-danger'>
													<i className='fa-solid fa-trash'></i> 
												</button>
											</td>
										</tr>
										))								
									}
								</tbody>
							</table>
						</div>
					</div>
				</div>
				
			</div>
			<div id='modalResults' className='modal fade' haria-hidden='true'>
				<div className='modal-dialog'>
					<div className='modal-content'>
						<div className='modal-header'>
							<label className='h5'>{title}</label>
							<button type='button' className='btn-close' data-bs-dismiss='modal' aria-label='Close'></button>
						</div>
						<div className='modal-body'>
							<input type='hidden' id='id'></input>
							<div className='input-group mb-3'>
								<span className='input-group-text'><i className='fa-solid fa-gift'></i></span>
								<input type='text' id='nombre' className='form-control' placeholder='Nombre' value={name} onChange={(e)=> setName(e.target.value)}></input>
							</div>
							<div className='input-group mb-3'><i className='fa-solid fa-doc'></i>
								<input type='text' id='descripcion' className='form-control' placeholder='Descripcion' value={description} onChange={(e)=> setDescription(e.target.value)}></input>
								<span className='input-group-text'></span>
							</div>
							<div className='input-group mb-3'>
								<span className='input-group-text'><i className='fa-solid fa-bill'></i></span>
								<input type='text' id='precio' className='form-control' placeholder='Precio' value={price} onChange={(e)=> setPrice(e.target.value)}></input>
							</div>	
							
							<div className='d-grid col-6 mx-auto'>
								<button onClick={()=>validar()} className='btn btn-success'>
								 <i className='fa-solid fa-floppy-disk'></i>Guardar
								</button> 
							</div>							
						</div>
						<div className='modal-footer'>
							<button type='button' id='close' className='btn btn-secundary' data-bs-dismiss='modal'>Cerrar</button>
						</div>
					</div>
